private void startUploadTest() {

    progressBar.setVisibility(View.VISIBLE);

    startButton.setEnabled(false);

    statusText.setText("Uploading test log to GitHub...");

    new Thread(new Runnable() {

        @Override
        public void run() {

            final boolean success =
                uploadTestFile();

            runOnUiThread(new Runnable() {

                @Override
                public void run() {

                    progressBar.setVisibility(View.GONE);

                    if (success) {

                        statusText.setText(
                            "✓ Upload successful.\n\n"
                            + "GitHub backup service is now active."
                        );

                        Prefs.setBoolean(
                            context,
                            "service_started",
                            true
                        );

                        SyncJobService.scheduleNext(
                            context
                        );
                    }
                    else {

                        statusText.setText(
                            "✗ Upload failed.\n\n"
                            + "Check GitHub token or internet."
                        );

                        startButton.setEnabled(true);
                    }
                }
            });
        }
    }).start();
}
